class Map:
    def __init__(self, jeux):
        self.jeux = jeux
        self.mini_map= mini_map
        self.world_map= {}
        self.get_map()

    def get_map(self):
        for i, col in enumerate(self.mini_map): # collone par collone
            for j, valeur in enumerate(col): # valeur dans chaque collone
                if valeur:
                    self.world_map[(j,i)] = valeur # on recupere emplacement de chaque mur
    def draw(self):
        [pg.draw.rect(self.jeux.screen, darkgray, (pos[0]*100, pos[1]* 100,100,100),2) for pos in self.world_map] # on dessins un carrer a chaque emplacement de world map



